import React, { component, useState } from 'react';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';
import { NameForm } from './helperUIcomponents';
//import './../App.css';



export default function HomePage(props) {

    const [redirect, setRedirect] = useState(false);

    const toMakeQuiz = () => {
        setRedirect(true);
    }

    if (redirect) {
        return <Redirect push to="/quizOverview" />;
    }

    useState({
        quiz_id : 1, //to accesss quiz to play
        maker_id: 1, //to access quiz to edit and mark responses
        num_rounds : 1,
        quiz : []})
    /*----------Init Quiz Object----------*/

    const initQuiz = () => {
        let data = Object.assign({}, props.quiz)
        data.quiz_id = Math.round(Math.random() * 10000)
        props.setQuiz(data)
    }

    return (
        <div className="full-screen-container">
            <div className="central-container">  
                <Button variant="primary" size='lg' onClick={toMakeQuiz}>Make Quiz</Button>
                <div className="join-quiz">
                    <h1>Join a Quiz!</h1>
                </div>
                <div> 
                    <NameForm fieldText="Quiz ID " />
                </div>               
            </div>        
        </div>
    );
}
