import React, { component, useState } from 'react';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';
import { NameForm } from './helperUIcomponents';
//import './../App.css';
import Button from 'react-bootstrap/Button';


export default function HomePage(props) {

    const [redirect, setRedirect] = useState(false);

   /*----------Init Quiz Object----------*/

    const initQuiz = () => {
        props.setQuiz({
            quiz_id : Math.round(Math.random() * 10000), //to accesss quiz to play
            maker_id: Math.round(Math.random() * 10000), //to access quiz to edit and mark responses
            num_rounds : 1,
            quiz : []
        })
    }

    const toMakeQuiz = () => {
        initQuiz();
        setRedirect(true);
    }

    if (redirect) {
        return <Redirect push to="/quizOverview" />;
    }


    return (
        <div className="full-screen-container">
            <div className="central-container">  
                <Button variant="primary" size='lg' onClick={toMakeQuiz}>Make Quiz</Button>
                <div className="join-quiz">
                    <h1>Join a Quiz!</h1>
                </div>
                <div> 
                    <NameForm fieldText="Quiz ID " />
                </div>               
            </div>        
        </div>
    );
}
