import React, { component, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Redirect} from 'react-router';
import { NameForm } from './helperUIcomponents.js';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import BootstrapTable from 'react-bootstrap-table-next';
import cellEditFactory from 'react-bootstrap-table2-editor';
import MaterialTable from 'material-table';

export function MaterialTableDemo(props) {


    return (
        <div style={{padding:50}}>

            <MaterialTable
                title="Editable Example"
                columns={props.state.columns}
                data={props.state.data}
                options = {{
                    search:false,
                    paging:false,
                }}
                editable={{
                onRowAdd: (newData) =>
                    new Promise((resolve) => {
                    setTimeout(() => {
                        resolve(); props.setState((prevState) => {
                        const data = [...prevState.data];
                        data.push(newData);
                        return { ...prevState, data };
                        });
                    }, 0);
                    }),
                onRowUpdate: (newData, oldData) =>
                    new Promise((resolve) => {
                    setTimeout(() => {
                        resolve();
                        if (oldData) {
                        props.setState((prevState) => {
                            const data = [...prevState.data];
                            data[data.indexOf(oldData)] = newData;
                            return { ...prevState, data };
                        });
                        }
                    }, 0);
                    }),
                onRowDelete: (oldData) =>
                    new Promise((resolve) => {
                    setTimeout(() => {
                        resolve();
                        props.setState((prevState) => {
                        const data = [...prevState.data];
                        data.splice(data.indexOf(oldData), 1);
                        return { ...prevState, data };
                        });
                    }, 0);
                    }),
                }}
            />
        </div>
    );
  }


export default function MakeRound(props) {

    const [numRows, setNumRows] = useState(1);
    const [roundData, setRoundData] = useState([
        { 'qn' : 1, 'question':"First Question", 'answer':"first answer"}
    ]);
    const [columns, setColumns] = useState([
        { title: 'Question Number', field: 'qn' },
        { title: 'Question', field: 'question' },
        { title: 'Answer', field: 'answer'}
      ])    

     const [state, setState] = useState({ columns: [
        //{ title: 'Quesition Number', field: 'tableData.id', type:'numeric', editable:false },
        {title: 'Question Number', render:(rowData) => rowData.tableData.id },
        { title: 'Question', field: 'question' },
        { title: 'Answer', field: 'answer' },
      ],
      data: [
        {  question:'Input Question' , answer:'Input Answer' },
      ],
    });

    const [homeRedirect, setHomeRedirect] = useState(false);
    const [quizOverviewRedirect, setQuizOverviewRedirect] = useState(false);

    useEffect( () => {
        if(props.edit) {
            //api call
        } else {
            setRoundData([{id: numRows, Question: 'Click to edit', Answer: 'Click to edit'}]);
            console.log("use effecy triggered")
        }
    },[])

    /*----------Navigation Helpers Start----------*/
    const toHome = () => {
        setHomeRedirect(true);
    }
    const toQuizOverview = () => {
        setQuizOverviewRedirect(true);
    }

    if (homeRedirect) {
        return <Redirect push to="/" />;
    }
    if (quizOverviewRedirect) {
        return <Redirect push to="/quizOverview" />;
    }

    const printProducts = () => {
        console.log(state.data);
    }

    return(
        <div className="full-screen-container">  
            <div className ="upper-left-container">
                <div className="central" style={{height:400}}>
                    <Button variant="primary" size="lg" onClick={toHome}>
                        Home
                    </Button>
                    <div className="padded-container">
                        <Button variant="primary" size='lg' onClick={toQuizOverview}>Overview</Button>
                    </div>
                </div>
            </div>
            <div className="central">
            <MaterialTableDemo 
                state={state}
                setState={setState}    
            />
            <Button variant="primary" size='lg' onClick={printProducts}>Print Products</Button>
            </div>
        </div>
    );
}
